import React, { useState, useEffect } from 'react';

const Header = ({ onNavigate }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const navLinks = [
    { name: 'About', href: '#about' },
    { name: 'Themes', href: '#themes' },
    { name: 'Blog', href: '#blog' },
    { name: 'Books', href: '#books' },
    { name: 'Contact', href: '#contact' },
  ];

  return (
    <header className={`sticky top-0 z-50 transition-all duration-300 ${isScrolled ? 'bg-white shadow-md' : 'bg-transparent'}`}>
      <div className="container mx-auto px-6 py-4 flex justify-between items-center">
        <button onClick={() => onNavigate('home')} className="focus:outline-none">
          <img src="/images/logo.png" alt="Quiet Strength Logo" className="h-12" />
        </button>
        <nav className="hidden md:flex space-x-10">
          {navLinks.map(link => (
            <a key={link.name} href={link.href} className="text-brand-dark font-semibold hover:text-brand-emphasis transition-colors duration-300">{link.name}</a>
          ))}
        </nav>
        <div className="md:hidden">
          <button onClick={() => setIsOpen(!isOpen)} className="text-brand-dark focus:outline-none">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={!isOpen ? "M4 6h16M4 12h16M4 18h16" : "M6 18L18 6M6 6l12 12"}></path>
            </svg>
          </button>
        </div>
      </div>
      {isOpen && (
        <div className="md:hidden bg-white shadow-lg">
          {navLinks.map(link => (
            <a key={link.name} href={link.href} className="block px-6 py-3 text-brand-dark hover:bg-brand-light" onClick={() => setIsOpen(false)}>{link.name}</a>
          ))}
        </div>
      )}
    </header>
  );
};

export default Header;
