// src/App.js

import React, { useState } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import Themes from './components/Themes';
import Blog from './components/Blog'; // This is the blog *preview section* on your homepage
import Books from './components/Books';
import Newsletter from './components/Newsletter';
import Testimonials from './components/Testimonials';
import Footer from './components/Footer';

// The only page component App.js needs to know about!
import BlogPage from './pages/BlogPage';

function App() {
  // This state only tracks if we are on 'home' or 'blog'
  const [activePage, setActivePage] = useState({ page: 'home', category: null });

  const navigateTo = (page, category = null) => {
    setActivePage({ page, category });
    window.scrollTo(0, 0);
  };

  return (
    <div className="bg-gray-50">
      <Header />
      <main>
        {activePage.page === 'home' ? (
          // If the page is 'home', show all the homepage sections
          <>
            <Hero />
            {/* This button will now take the user to the main blog page */}
            <Blog onNavigate={() => navigateTo('blog')} />
            <About />
            <Themes onNavigate={navigateTo} />
            <Books />
            <Newsletter />
            <Testimonials />
          </>
        ) : (
          // If the page is 'blog', show the BlogPage component.
          // We pass it a function to get back home.
          <BlogPage
            onBack={() => navigateTo('home')}
            category={activePage.category}
          />
        )}
      </main>
      <Footer />
    </div>
  );
}

export default App;
