import React, { useState, useEffect } from 'react';

const Header = ({ onNavigate, activePage }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 120);
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const navLinks = [
    { name: 'About', page: 'about' },
    { name: 'Themes', page: 'themes' },
    { name: 'Blog', page: 'blog' },
    { name: 'Books', page: 'books' },
    { name: 'Contact', page: 'contact' },
  ];

  return (
    <header className={`sticky top-0 z-50 transition-all duration-300 ${isScrolled ? 'h-[76px] bg-white shadow-md' : 'h-[104px] bg-transparent'}`}>
      <a href="#main-content" className="sr-only focus:not-sr-only">Skip to main content</a>
      <div className="container mx-auto px-6 flex justify-between items-center h-full">
        <button onClick={() => onNavigate('home')} className="focus:outline-none">
          <img src="/images/logo.png" alt="Quiet Strength Logo" className={`transition-all duration-300 ${isScrolled ? 'h-10' : 'h-14'}`} />
        </button>
        <nav className="hidden lg:flex items-center gap-10">
          {navLinks.map(link => (
            <a
              key={link.name}
              href={`#${link.page}`}
              onClick={(e) => {
                e.preventDefault();
                onNavigate(link.page);
              }}
              className={`relative text-brand-dark font-semibold hover:text-brand-emphasis transition-all duration-300 group ${isScrolled ? 'text-base py-2' : 'text-lg py-3'} ${activePage.page === link.page ? 'active' : ''}`}
              aria-current={activePage.page === link.page ? 'page' : undefined}
            >
              {link.name}
              <span className="absolute bottom-0 left-0 w-full h-1 bg-brand-emphasis transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300 ease-out origin-left group-[.active]:scale-x-100"></span>
            </a>
          ))}
        </nav>
        <div className="lg:hidden">
          <button onClick={() => setIsOpen(!isOpen)} className="text-brand-dark focus:outline-none">
            <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={!isOpen ? "M4 6h16M4 12h16M4 18h16" : "M6 18L18 6M6 6l12 12"}></path>
            </svg>
          </button>
        </div>
      </div>
      {/* Mobile Menu */}
      <div className={`fixed top-0 right-0 h-full w-full bg-white z-40 transform transition-transform duration-300 ease-in-out ${isOpen ? 'translate-x-0' : 'translate-x-full'} lg:hidden`}>
        <div className="flex justify-end p-6">
          <button onClick={() => setIsOpen(false)} className="text-brand-dark focus:outline-none">
            <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        <nav className="flex flex-col items-center justify-center h-full -mt-16 space-y-8">
          {navLinks.map(link => (
            <a
              key={link.name}
              href={`#${link.page}`}
              onClick={(e) => {
                e.preventDefault();
                onNavigate(link.page);
                setIsOpen(false);
              }}
              className="text-2xl text-brand-dark font-semibold hover:text-brand-emphasis transition-colors duration-300"
            >
              {link.name}
            </a>
          ))}
        </nav>
      </div>
    </header>
  );
};

export default Header;
