import React from 'react';

import { sortedBlogPosts, categories } from '../blogData';
import BlogCard from './BlogCard';

const Blog = ({ onNavigate }) => {
  const latestPost = sortedBlogPosts[0];
  const recentPosts = sortedBlogPosts.slice(1, 4);

  return (
    <section id="blog" className="py-24 bg-brand-light">
      <div className="container mx-auto px-6">
        <div className="text-left mb-16">
          <h2 className="text-4xl md:text-5xl font-bold text-brand-dark">Latest Insights & Articles</h2>
        </div>

        {/* Featured Article */}
        <div className="bg-white rounded-lg shadow-lg p-8 mb-16 flex flex-col md:flex-row items-center">
          <div className="md:w-1/3 mb-8 md:mb-0 md:pr-12">
            <img 
              src={latestPost.image} 
              alt={`Featured article: ${latestPost.title} - self-help guide for introverted women`} 
              width="400" 
              height="250" 
              loading="lazy"
              className="rounded-lg shadow-md w-full h-auto"
            />
          </div>
          <div className="md:w-2/3">
            <h3 className="text-3xl md:text-4xl font-bold text-brand-dark mb-4">{latestPost.title}</h3>
            <p className="text-brand-primary mb-6">{latestPost.description}</p>
            <button onClick={() => onNavigate('blog', null, latestPost.slug)} className="text-brand-emphasis font-semibold hover:underline">
              Read the Full Article &rarr;
            </button>
          </div>
        </div>

        {/* Recent Posts Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12 mb-16">
          {recentPosts.map((post) => (
            <BlogCard
              key={post.slug}
              post={post}
              onReadMore={() => onNavigate('blog', null, post.slug)}
            />
          ))}
        </div>

        {/* Category Navigation */}
        <div className="text-center mb-12">
          <h3 className="text-2xl font-bold text-brand-dark mb-6">Browse by Category</h3>
          <div className="flex flex-wrap justify-center gap-4 mb-8">
            {Object.values(categories).map((categoryName) => (
              <button
                key={categoryName}
                onClick={() => onNavigate('blog', categoryName)}
                className="px-6 py-3 rounded-full font-semibold bg-brand-emphasis text-white hover:bg-opacity-90 transition-colors"
              >
                {categoryName}
              </button>
            ))}
          </div>
        </div>

        <div className="text-center">
          <button onClick={() => onNavigate('blog')} className="bg-brand-dark text-white font-bold py-4 px-10 rounded-full hover:bg-opacity-90 transition duration-300 transform hover:scale-105">
            View All Articles
          </button>
        </div>
      </div>
    </section>
  );
};

export default Blog;
