  return (
    <section id="about" className="py-20 bg-white">
      <div className="container mx-auto px-6">
        <div className="max-w-4xl mx-auto text-center">
          <div className="mb-8">
            <img 
              src="https://placehold.co/150x150/e2e8f0/4a5568?text=Author" 
              alt="[Your Name], founder of Quiet Strength" 
              className="w-40 h-40 rounded-full mx-auto shadow-lg"
            />
          </div>
          <h2 className="text-3xl md:text-4xl font-bold text-slate-800 mb-4">About Quiet Strength</h2>
          <p className="text-slate-600 text-lg mb-4">
            Welcome to Quiet Strength, your sanctuary and roadmap for thriving as an introverted woman in a world that often feels too loud. Iâ€™m [Your Name], and I created this space to empower women like us to embrace our introversion as a superpower.
          </p>
          <p className="text-slate-600 text-lg">
            My mission is to provide you with actionable advice, insightful articles, and supportive resources to help you build unshakeable confidence, manage your energy effectively, and create a life of purpose and financial freedom. Let's embark on this journey together.
          </p>
        </div>
      </div>
    </section>
  );
};

export default About;
