import React, { useState } from 'react';
import BlogPostSayingNo from '../BlogPostSayingNo'; // Make sure this path is correct

// A simple component for your blog cards
const BlogCard = ({ title, description, onReadMore }) => (
  <div className="max-w-sm rounded overflow-hidden shadow-lg p-6 bg-white border border-gray-200">
    <h3 className="font-bold text-xl mb-2 text-slate-800">{title}</h3>
    <p className="text-gray-700 text-base mb-4">
      {description}
    </p>
    <button onClick={onReadMore} className="font-semibold text-indigo-600 hover:text-indigo-800">
      Read More &rarr;
    </button>
  </div>
);


// This is the main component that controls everything
const BlogSection = () => {
  // State to track which post is selected. null means show the list.
  const [selectedPost, setSelectedPost] = useState(null);

  // Function to handle clicking "Read More"
  const handleReadMoreClick = (postSlug) => {
    setSelectedPost(postSlug);
  };

  // Function to handle clicking the "Back" button from within a blog post
  const handleBackClick = () => {
    setSelectedPost(null); // Go back to the list view
  };

  // If a post is selected, render that specific post component
  if (selectedPost === 'art-of-saying-no') {
    return <BlogPostSayingNo onBack={handleBackClick} />;
  }

  // If no post is selected (the default state), render the list of cards
  return (
    <div className="container mx-auto px-6 py-12">
      <h2 className="text-4xl font-bold text-center mb-12 text-slate-800">From the Blog</h2>
      <div className="flex justify-center gap-8">
        {/* This is the card from your image. We wire its onReadMore prop here. */}
        <BlogCard
          title="The Art of Saying No: A Guide for People-Pleasers"
          description="Reclaim your time and energy by learning how to set boundaries with grace and confidence."
          onReadMore={() => handleReadMoreClick('art-of-saying-no')}
        />

        {/* Another example card */}
        <BlogCard
          title="Networking for Introverts"
          description="Ditch the pressure and learn how to build authentic connections in a way that feels natural."
          onReadMore={() => handleReadMoreClick('networking-for-introverts')}
          // NOTE: You would need to create a BlogPostNetworking component for this one.
        />
      </div>
    </div>
  );
};

export default BlogSection;
