import React from 'react';

const featuredArticle = {
  title: "How to Prevent Professional Burnout: A Woman's Essential Guide to Sustainable Success & Well-being",
  excerpt: "In a world that glorifies hustle, learn how to identify the early signs of burnout and implement sustainable strategies to protect your energy, prioritize your well-being, and achieve success on your own terms. This guide is essential for any introverted woman navigating the modern workplace.",
  link: "#"
};

const recentPosts = [
  {
    title: "The Art of Saying No: A Guide for People-Pleasers",
    excerpt: "Reclaim your time and energy by learning how to set boundaries with grace and confidence.",
    link: "#"
  },
  {
    title: "Networking for Introverts: Building Authentic Connections",
    excerpt: "Ditch the awkward small talk and discover how to build a powerful network that feels natural.",
    link: "#"
  },
  {
    title: "Creating Your Ideal 'Recharge' Routine",
    excerpt: "Design a personalized routine to restore your energy and thrive in your daily life.",
    link: "#"
  }
];

const Blog = () => {
  return (
    <section id="blog" className="py-20 bg-white">
      <div className="container mx-auto px-6">
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold text-slate-800">Latest Insights & Articles</h2>
        </div>

        {/* Featured Article */}
        <div className="bg-gray-100 rounded-lg shadow-lg p-8 mb-12 flex flex-col md:flex-row items-center">
          <div className="md:w-2/3">
            <h3 className="text-2xl md:text-3xl font-bold text-slate-800 mb-4">{featuredArticle.title}</h3>
            <p className="text-slate-600 mb-6">{featuredArticle.excerpt}</p>
            <a href={featuredArticle.link} className="bg-indigo-600 text-white font-bold py-3 px-6 rounded-full hover:bg-indigo-700 transition duration-300">
              Read the Full Article
            </a>
          </div>
          <div className="md:w-1/3 mt-8 md:mt-0 md:pl-8">
            <img src="https://placehold.co/600x400/e2e8f0/4a5568?text=Featured" alt="Featured article illustration" className="rounded-lg shadow-md"/>
          </div>
        </div>

        {/* Recent Posts Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
          {recentPosts.map((post, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 flex flex-col">
              <h4 className="text-xl font-bold text-slate-800 mb-2 flex-grow">{post.title}</h4>
              <p className="text-slate-600 mb-4">{post.excerpt}</p>
              <a href={post.link} className="text-indigo-600 hover:text-indigo-800 font-semibold self-start">Read More &rarr;</a>
            </div>
          ))}
        </div>

        <div className="text-center">
          <a href="#" className="bg-slate-800 text-white font-bold py-3 px-8 rounded-full hover:bg-slate-900 transition duration-300">
            View All Articles
          </a>
        </div>
      </div>
    </section>
  );
};

export default Blog;
